'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const setMinutes = generic.function({name: 'setMinutes', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.setminutes
setMinutes.method([_, _, _, is(Date)],
  (min, sec, ms, date) =>
    date.setMinutes(min, sec, ms)
)

module.exports = setMinutes
